    fun init (playerId: String,
              onSuccess: (() -> Unit)? = null,
              onFailure: ((String) -> Unit)? = null,
              onDisconnect: (() -> Unit)? = null,
              onMessage: ((String) -> Unit)? = null) {
        this.playerId = playerId
