        }
    }

    fun logout() {
        repository.clearStoredPlayerInfo()
        webSocketManager.disconnect()
        _authState.value = AuthState.Idle
    }

    private fun checkStoredAuth() {
        val playerInfo = repository.getStoredPlayerInfo()
