import androidx.compose.foundation.layout.ColumnScope import androidx.compose.foundation.layout.Row import androidx.compose.foundation.layout.RowScope import androidx.compose.foundation.shape.RoundedCornerShape import androidx.compose.material3.Button import androidx.compose.material3.ButtonColors import androidx.compose.material3.ButtonDefaults import androidx.compose.material3.Card import androidx.compose.material3.CardDefaults import androidx.compose.material3.MaterialTheme import androidx.compose.material3.OutlinedTextField import androidx.compose.material3.Text import androidx.compose.material3.TextFieldDefaults import androidx.compose.runtime.Composable import androidx.compose.ui.Modifier import androidx.compose.ui.graphics.Color import androidx.compose.ui.text.TextStyle import androidx.compose.ui.text.style.TextAlign import androidx.compose.ui.text.style.TextOverflow import androidx.compose.ui.unit.dp
@Composable fun CoffeeButton( onClick: () -> Unit, modifier: Modifier = Modifier, enabled: Boolean = true, colors: ButtonColors = ButtonDefaults.buttonColors( containerColor = CoffeeBrownSoft, // Noch etwas kr辰ftigerer, aber weicher Braunton f端r Buttons contentColor = MaterialTheme.colorScheme.onPrimary ), content: @Composable () -> Unit ) { Button( onClick = onClick, modifier = modifier, enabled = enabled, shape = RoundedCornerShape(16.dp), colors = colors ) { Row { content() } } }
@Composable fun CoffeeCard( modifier: Modifier = Modifier, containerColor: Color = MaterialTheme.colorScheme.surface, contentColor: Color = MaterialTheme.colorScheme.onSurface, content: @Composable ColumnScope.() -> Unit ) { Card( modifier = modifier, shape = RoundedCornerShape(20.dp), colors = CardDefaults.cardColors( containerColor = containerColor, contentColor = contentColor ), content = content ) }
@Composable fun CoffeeTextField( value: String, onValueChange: (String) -> Unit, modifier: Modifier = Modifier, label: String? = null, isError: Boolean = false, singleLine: Boolean = true, enabled: Boolean = true ) { OutlinedTextField( value = value, onValueChange = onValueChange, modifier = modifier, label = label?.let { { Text(it) } }, singleLine = singleLine, enabled = enabled, isError = isError, shape = RoundedCornerShape(12.dp), colors = TextFieldDefaults.colors( focusedContainerColor = MaterialTheme.colorScheme.surface, unfocusedContainerColor = MaterialTheme.colorScheme.surface, disabledContainerColor = MaterialTheme.colorScheme.surface, errorContainerColor = MaterialTheme.colorScheme.surface, focusedTextColor = MaterialTheme.colorScheme.onSurface, unfocusedTextColor = MaterialTheme.colorScheme.onSurface, disabledTextColor = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.5f), errorTextColor = MaterialTheme.colorScheme.error, focusedIndicatorColor = MaterialTheme.colorScheme.primary, unfocusedIndicatorColor = MaterialTheme.colorScheme.secondary, disabledIndicatorColor = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.2f), errorIndicatorColor = MaterialTheme.colorScheme.error, focusedLabelColor = MaterialTheme.colorScheme.primary, unfocusedLabelColor = MaterialTheme.colorScheme.onSurface, disabledLabelColor = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.5f), errorLabelColor = MaterialTheme.colorScheme.error ) ) }
@Composable fun CoffeeText( text: String, modifier: Modifier = Modifier, style: TextStyle = MaterialTheme.typography.bodyLarge, color: Color = MaterialTheme.colorScheme.onSurface, maxLines: Int = Int.MAX_VALUE, overflow: TextOverflow = TextOverflow.Clip, textAlign: TextAlign? = null ) { Text( text = text, modifier = modifier, style = style, color = color, maxLines = maxLines, overflow = overflow, textAlign = textAlign ) }

package app.chesspresso.ui.theme
import androidx.compose.foundation.layout.ColumnScope import androidx.compose.foundation.layout.Row import androidx.compose.foundation.layout.RowScope import androidx.compose.foundation.shape.RoundedCornerShape import androidx.compose.material3.Button import androidx.compose.material3.ButtonColors import androidx.compose.material3.ButtonDefaults import androidx.compose.material3.Card import androidx.compose.material3.CardDefaults import androidx.compose.material3.MaterialTheme import androidx.compose.material3.OutlinedTextField import androidx.compose.material3.Text import androidx.compose.material3.TextFieldDefaults import androidx.compose.runtime.Composable import androidx.compose.ui.Modifier import androidx.compose.ui.graphics.Color import androidx.compose.ui.text.TextStyle import androidx.compose.ui.text.style.TextAlign import androidx.compose.ui.text.style.TextOverflow import androidx.compose.ui.unit.dp import androidx.compose.ui.unit.sp import androidx.compose.ui.text.font.FontWeight
@Composable fun CoffeeButton( onClick: () -> Unit, modifier: Modifier = Modifier, enabled: Boolean = true, colors: ButtonColors = ButtonDefaults.buttonColors( containerColor = CoffeeBrownSoft, // Noch etwas kr辰ftigerer, aber weicher Braunton f端r Buttons contentColor = MaterialTheme.colorScheme.onPrimary ), content: @Composable () -> Unit ) { Button( onClick = onClick, modifier = modifier, enabled = enabled, shape = RoundedCornerShape(16.dp), colors = colors ) { Row { content() } } }
@Composable fun CoffeeCard( modifier: Modifier = Modifier, containerColor: Color = MaterialTheme.colorScheme.surface, contentColor: Color = MaterialTheme.colorScheme.onSurface, content: @Composable ColumnScope.() -> Unit ) { Card( modifier = modifier, shape = RoundedCornerShape(20.dp), colors = CardDefaults.cardColors( containerColor = containerColor, contentColor = contentColor ), content = content ) }
@Composable fun CoffeeTextField( value: String, onValueChange: (String) -> Unit, modifier: Modifier = Modifier, label: String? = null, isError: Boolean = false, singleLine: Boolean = true, enabled: Boolean = true ) { OutlinedTextField( value = value, onValueChange = onValueChange, modifier = modifier, label = label?.let { { Text(it) } }, singleLine = singleLine, enabled = enabled, isError = isError, shape = RoundedCornerShape(12.dp), colors = TextFieldDefaults.colors( focusedContainerColor = MaterialTheme.colorScheme.surface, unfocusedContainerColor = MaterialTheme.colorScheme.surface, disabledContainerColor = MaterialTheme.colorScheme.surface, errorContainerColor = MaterialTheme.colorScheme.surface, focusedTextColor = MaterialTheme.colorScheme.onSurface, unfocusedTextColor = MaterialTheme.colorScheme.onSurface, disabledTextColor = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.5f), errorTextColor = MaterialTheme.colorScheme.error, focusedIndicatorColor = MaterialTheme.colorScheme.primary, unfocusedIndicatorColor = MaterialTheme.colorScheme.secondary, disabledIndicatorColor = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.2f), errorIndicatorColor = MaterialTheme.colorScheme.error, focusedLabelColor = MaterialTheme.colorScheme.primary, unfocusedLabelColor = MaterialTheme.colorScheme.onSurface, disabledLabelColor = MaterialTheme.colorScheme.onSurface.copy(alpha = 0.5f), errorLabelColor = MaterialTheme.colorScheme.error ) ) }
@Composable fun CoffeeText( text: String, modifier: Modifier = Modifier, style: TextStyle = MaterialTheme.typography.bodyLarge, color: Color = MaterialTheme.colorScheme.onSurface, maxLines: Int = Int.MAX_VALUE, overflow: TextOverflow = TextOverflow.Clip, textAlign: TextAlign? = null ) { Text( text = text, modifier = modifier, style = style.copy(fontFamily = Montserrat), color = color, maxLines = maxLines, overflow = overflow, textAlign = textAlign ) }
@Composable fun CoffeeHeadlineText( text: String, modifier: Modifier = Modifier, fontSizeSp: Int = 28, fontWeight: FontWeight = FontWeight.Bold, color: Color = MaterialTheme.colorScheme.onSurface, textAlign: TextAlign? = null, maxLines: Int = Int.MAX_VALUE, overflow: TextOverflow = TextOverflow.Clip ) { Text( text = text, modifier = modifier, style = TextStyle( fontFamily = Montserrat, fontWeight = fontWeight, fontSize = fontSizeSp.sp ), color = color, textAlign = textAlign, maxLines = maxLines, overflow = overflow ) }

package app.chesspresso.ui.theme
import androidx.compose.material3.MaterialTheme import androidx.compose.material3.darkColorScheme import androidx.compose.material3.lightColorScheme import androidx.compose.runtime.Composable
private val DarkColorScheme = darkColorScheme( primary = CoffeeCreme, onPrimary = CoffeeBrownContrast, background = CoffeeBrownDark, onBackground = CoffeeCremeLight, surface = CoffeeBrownContrast, onSurface = CoffeeCremeLight, secondary = CoffeeOrange, tertiary = CoffeeRust )
private val LightColorScheme = lightColorScheme( primary = CoffeeBrownDark, onPrimary = CoffeeCremeLight, background = CoffeeCremeLight, onBackground = CoffeeBrownContrast, surface = CoffeeCremeMid, // jetzt mittleres Creme f端r Cards onSurface = CoffeeBrownContrast, secondary = CoffeeOrange, tertiary = CoffeeRust )
@Composable fun ChessPressoAppTheme( darkTheme: Boolean = false, // Default ist jetzt helles Theme dynamicColor: Boolean = false, // Dynamic Color deaktiviert content: @Composable () -> Unit ) { val colorScheme = if (darkTheme) DarkColorScheme else LightColorScheme MaterialTheme( colorScheme = colorScheme, typography = Typography, content = content ) }

package app.chesspresso.ui.theme
import androidx.compose.material3.MaterialTheme import androidx.compose.material3.darkColorScheme import androidx.compose.material3.lightColorScheme import androidx.compose.runtime.Composable
private val DarkColorScheme = darkColorScheme( primary = CoffeeCreme, onPrimary = CoffeeBrownContrast, background = CoffeeBrownDark, onBackground = CoffeeCremeLight, surface = CoffeeBrownContrast, onSurface = CoffeeCremeLight, secondary = CoffeeOrange, tertiary = CoffeeRust )
private val LightColorScheme = lightColorScheme( primary = CoffeeBrownDark, onPrimary = CoffeeCremeLight, background = CoffeeCremeLight, onBackground = CoffeeBrownContrast, surface = CoffeeCremeMid, // jetzt mittleres Creme f端r Cards onSurface = CoffeeBrownContrast, secondary = CoffeeOrange, tertiary = CoffeeRust )
@Composable fun ChessPressoAppTheme( darkTheme: Boolean = false, // Default ist jetzt helles Theme dynamicColor: Boolean = false, // Dynamic Color deaktiviert content: @Composable () -> Unit ) { val colorScheme = if (darkTheme) DarkColorScheme else LightColorScheme MaterialTheme( colorScheme = colorScheme, typography = CoffeeTypography, content = content ) }
